#include <iostream>//Realizar un programa que lea un número por teclado y muestre por pantalla la serie cuyo primer término es el número y los siguientes términos son la suma de los divisores del término anterior. Por ejemplo, para el número 300 el programa mostraría la secuencia 300 568 512 511 81 40 50 43 1 0 ya que 568 es la suma de los divisores de 300, 512 es la suma de los divisores de 568, 81 es la suma de los divisores de 511 y así sucesivamente. El programa terminará cuando en la secuencia se repitan dos números consecutivos.using namespace std;int main (){	unsigned n;	unsigned d;	cout<<"DIme el numero"<<endl;	cin>>d;          	do{        	 cout<<d<<" ";	 n=d;	 	 d=0;	 for(unsigned i=1; i<n; i++){		if(n%d==0){			d=d+i;		}		 }	}while (n!=d);			}	cout<<endl;return 0;}